/*
import React, {useContext} from 'react';
import {observer} from "mobx-react-lite"

import {Pagination} from "react-bootstrap";
import {Context} from "../../index";

const PaginationBamf = observer(() => {

    const {rows} = useContext(Context)
    const pageCount = Math.ceil(rows.totalCount / rows.limit)
    const pages = []

    for (let i=0; i<pageCount; i++) {
        pages.push(i+1)
    }

    return (
        <Pagination className="mx-auto mt-2">
            {pages.map(page =>
                <Pagination.Item
                    key={page}
                    active={rows.page === page}
                    onClick={()=> rows.setPage(page)}
                >{page}</Pagination.Item>
            )}
        </Pagination>
    );
});

export default PaginationBamf;*/
