import {Table} from "react-bootstrap";

import React, {useContext, useEffect, useState} from "react";
import {observer} from "mobx-react-lite";
import {fetchRows} from "../../http/dataApi";
import Pagination_bamf from "../Pagination/Pagination_bamf";
import Filter from "../Filter/Filter";
import TableInput from "../TableInput/TableInput";
import {Context} from "../../index";

const Table_bamf = observer(() => {

    const {rows} = useContext(Context)

    const [tableRows, setTableRows] = useState([])
    const [currentPage, setCurrentPage] = useState(1)
    const [rowsPerPage] = useState(3)

    useEffect(() => {
        fetchRows().then(data => {
            setTableRows(data)
            rows.setRows(tableRows)
            console.log(data)


        })
    },[])


    const [sortedTableRows, setSortedTableRows] = useState([])

    const [order, setOrder] = useState("ASC")
    const sortByColumn = (column) => {

        //TODO: reduce algorithm difficulty
        if (column === "col_name") {
            if (order === "ASC") {
                const sorted = [...tableRows].sort((a,b) => a[column].toLowerCase() > b[column].toLowerCase() ? 1:-1)
                setSortedTableRows(sorted)
                setOrder("DSC")
            }
            if (order === "DSC") {
                const sorted = [...tableRows].sort((a,b) => a[column].toLowerCase() < b[column].toLowerCase() ? 1:-1)
                setSortedTableRows(sorted)
                setOrder("ASC")
            }
        }
        else {
            if (order === "ASC") {
                const sorted = [...tableRows].sort((a,b) => a[column] - b[column])
                setSortedTableRows(sorted)
                setOrder("DSC")
            }
            if (order === "DSC") {
                const sorted = [...tableRows].sort((a,b) => b[column] - a[column])
                setSortedTableRows(sorted)
                setOrder("ASC")
            }
        }
    }



    const lastRowIndex = currentPage * rowsPerPage
    const firstRowIndex = lastRowIndex - rowsPerPage
    const currentRows = sortedTableRows.slice(firstRowIndex, lastRowIndex)



    const paginate = pageNumber =>setCurrentPage(pageNumber)


    return (
        <div className="d-flex flex-row">
            <div>
                <TableInput/>
                <Table className="my-auto" striped bordered hover>
                    <thead>
                    <tr>
                        <th>
                            Date
                        </th>
                        <th
                            onClick={() => sortByColumn("col_name")}>Name
                        </th>
                        <th
                            onClick={() => sortByColumn("col_count")}>
                            Count
                        </th>
                        <th
                            onClick={() => sortByColumn("col_distance")}>
                            Distance
                        </th>
                    </tr>
                    </thead>
                    <tbody>
                    {currentRows.map(row => (
                        <tr key={row.col_id}>
                            <td className="col-3">{row.col_date}</td>
                            <td className="col-3">{row.col_name}</td>
                            <td className="col-3">{row.col_count}</td>
                            <td className="col-3">{row.col_distance}</td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
                <Pagination_bamf rowsPerPage={rowsPerPage} totalRows={tableRows.length} paginate={paginate}/>
            </div>
            <Filter  />
        </div>
    );
});

export default Table_bamf;
