import React, {useContext, useState} from 'react';
import {Card, Dropdown, DropdownButton, Form, InputGroup} from "react-bootstrap";
import {Context} from "../../index";

const Sorting = () => {

    const {rows} = useContext(Context)

    const [sortColumn, asd] = useState('')
    const [condition, setCondition] = useState('')
    const [valueToFilter, setValueToFilter] = useState('')


    return (
        <div className="ms-4">
            <h1>Sorting</h1>
            <Card className="">
                <Card.Body>
                    <DropdownButton
                        className="mb-2"
                        variant="outline-secondary"
                        title={"Sort column: " + sortColumn || "Sort Column"}
                        id="input-group-dropdown-1"
                    >
                        <Dropdown.Item onClick={()=> rows.setSortColumn('Name')}>Name</Dropdown.Item>
                        <Dropdown.Item onClick={()=> setSortColumn('Count')}>Count</Dropdown.Item>
                        <Dropdown.Item onClick={()=> setSortColumn('Distance')}>Distance</Dropdown.Item>
                    </DropdownButton>
                    <DropdownButton
                        className="mb-2"
                        variant="outline-secondary"
                        title={"Condition: " + condition || "Condition"}
                        id="input-group-dropdown-1"
                    >
                        <Dropdown.Item onClick={()=> setCondition('Equals')}>Equals</Dropdown.Item>
                        <Dropdown.Item onClick={()=> setCondition('Include')}>Include</Dropdown.Item>
                        <Dropdown.Item onClick={()=> setCondition('Over')}>Over</Dropdown.Item>
                        <Dropdown.Item onClick={()=> setCondition('Less then')}>Less then</Dropdown.Item>
                    </DropdownButton>
                    <InputGroup >
                        <Form.Control
                            placeholder="Value to filter"
                            aria-label="Value to filter"
                            aria-describedby="Value to filter"
                            value={valueToFilter} onChange={e => setValueToFilter(e.target.value)}
                        />
                    </InputGroup>
                </Card.Body>
            </Card>
        </div>
    );
};

export default Sorting;
