import {Table} from "react-bootstrap";

import React, {useContext, useEffect, useState} from "react";
import {observer} from "mobx-react-lite";
import {fetchRows} from "../../http/dataApi";
import Pagination_bamf from "../Pagination/Pagination_bamf";
import Sorting from "../Sorting/Sorting";
import TableInput from "../TableInput/TableInput";

const Table_bamf = observer(() => {
    const [tableRows, setTableRows] = useState([])
    const [currentPage, setCurrentPage] = useState(1)
    const [rowsPerPage] = useState(3)

    useEffect(() => {
        fetchRows().then(data => {
            setTableRows(data)
        })
    },[])

    const lastRowIndex = currentPage * rowsPerPage
    const firstRowIndex = lastRowIndex - rowsPerPage
    const currentRows = tableRows.slice(firstRowIndex, lastRowIndex)

    const paginate = pageNumber =>setCurrentPage(pageNumber)

    return (
        <div className="d-flex flex-row">
            <div>
                <TableInput/>
                <Table className="my-auto" striped bordered hover>
                    <thead>
                    <tr>
                        <th>Date</th>
                        <th>Name</th>
                        <th>Count</th>
                        <th>Distance</th>
                    </tr>
                    </thead>
                    <tbody>
                    {currentRows.map(row => (
                        <tr key={row.col_id}>
                            <td className="col-3">{row.col_date}</td>
                            <td className="col-3">{row.col_name}</td>
                            <td className="col-3">{row.col_count}</td>
                            <td className="col-3">{row.col_distance}</td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
                <Pagination_bamf rowsPerPage={rowsPerPage} totalRows={tableRows.length} paginate={paginate}/>
            </div>
            <Sorting/>
        </div>
    );
});

export default Table_bamf;
