import React, {useState} from 'react';
import {Button, Form, InputGroup} from "react-bootstrap";

const TableInput = () => {
    const [col_date, setDate] = useState(0)
    const [col_name, setName] = useState('')
    const [col_count, setCount] = useState(0)
    const [col_distance, setDistance] = useState(0)

    const getTable = async() => {
        try{
            const response = await fetch("http://localhost:5000/rows")
            const jsonData = await response.json()

            setRows(jsonData)
        }
        catch (err){
            console.error(err.message)
        }
    }

    const onSubmitForm = async e => {
        e.preventDefault();
        try {
            const body = {col_date, col_name, col_count, col_distance}
            const response = await fetch("http://localhost:5000/rows",
                {
                    method: "POST",
                    headers: {"Content-type": "application/json"},
                    body: JSON.stringify(body)
                });
            await getTable()
            console.log(response)
            console.log(body)
        }
        catch (err) {
            console.log(err.message)
        }
    }

    return (
        <div>
            <h1>Input</h1>
            <form onSubmit={onSubmitForm}>
                <InputGroup className="mb-2" >
                    <Form.Control
                        placeholder="date"
                        aria-label="date"
                        aria-describedby="date"
                        type="number"
                        value={col_date}
                        onChange={e =>  setDate(e.target.value)}
                    />
                    <Form.Control
                        placeholder="name"
                        aria-label="name"
                        aria-describedby="name"
                        type="text"
                        value={col_name}
                        onChange={e =>  setName(e.target.value)}
                    />
                    <Form.Control
                        placeholder="count"
                        aria-label="count"
                        aria-describedby="count"
                        type="number"
                        value={col_count}
                        onChange={e =>  setCount(e.target.value)}
                    />
                    <Form.Control
                        placeholder="distance"
                        aria-label="distance"
                        aria-describedby="distance"
                        type="number"
                        value={col_distance}
                        onChange={e =>  setDistance(e.target.value)}
                    />
                    <Button
                        variant="outline-secondary"
                        type="submit"
                    >
                        Add row
                    </Button>
                </InputGroup>
            </form>

        </div>
    );
};

export default TableInput;
