import {makeAutoObservable} from "mobx"

export default class DataStore{
    constructor() {
        this._sortColumn = []
        this._sortCondition = []
        this._sortValueToFilter = []

        makeAutoObservable(this)
    }

    setSortColumn(sortColumn){
        this._sortColumn = sortColumn
    }
    setSortCondition(sortCondition){
        this._sortCondition = sortCondition
    }
    setSortValueToFilter(sortValueToFilter){
        this._sortValueToFilter = sortValueToFilter
    }
    get sortColumn(){
        return this._sortColumn
    }
    get sortCondition(){
        return this._sortCondition
    }
    get sortValueToFilter(){
        return this._sortValueToFilter
    }
}
