import React, {useState, useEffect} from 'react';
import {Card, Container} from "react-bootstrap";
import Table_bamf from "./Components/Table/Table_bamf";
import Pagination_bamf from "./Components/Pagination/Pagination_bamf";
import Sorting from "./Components/Sorting/Sorting";



const App = () => {
    const [merchants, setMerchants] = useState(false);
    useEffect(() => {
        getMerchant();
    }, []);
    function getMerchant() {
        fetch('http://localhost:3001')
            .then(response => {
                return response.text();
            })
            .then(data => {
                setMerchants(data);
            });
    }
    function createMerchant() {
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');
        fetch('http://localhost:3001/merchants', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({name, email}),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getMerchant();
            });
    }
    function deleteMerchant() {
        let id = prompt('Enter merchant id');
        fetch(`http://localhost:3001/merchants/${id}`, {
            method: 'DELETE',
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getMerchant();
            });
    }
    return (
        <Container style={{height:window.innerHeight}} className="d-flex flex-row justify-content-center align-items-center">
            <div style={{maxWidth:1000}} className="d-flex flex-column w-100">
                <Table_bamf/>
                <Pagination_bamf/>
            </div>
            <Sorting/>
            <div>
                {merchants ? merchants : 'There is no merchant data available'}
                <button onClick={createMerchant}>Add merchant</button>
                <button onClick={deleteMerchant}>Delete merchant</button>
            </div>
        </Container>
    );
}
export default App
