import React, {useContext, useState} from 'react';
import {Card, Dropdown, DropdownButton, Form, InputGroup} from "react-bootstrap";
import {Context} from "../../index";
import {observer} from "mobx-react-lite";

const Sorting = observer(() => {
    const {rows} = useContext(Context)

    return (
        <div className="ms-4">
            <h1>Sorting</h1>
            <Card className="">
                <Card.Body>
                    <DropdownButton
                        className="mb-2"
                        variant="outline-secondary"
                        title={"Sort column: " + rows.sortColumn}
                        id="input-group-dropdown-1"
                    >
                        <Dropdown.Item onClick={()=> rows.setSortColumn('Name')}>Name</Dropdown.Item>
                        <Dropdown.Item onClick={()=> rows.setSortColumn('Count')}>Count</Dropdown.Item>
                        <Dropdown.Item onClick={()=> rows.setSortColumn('Distance')}>Distance</Dropdown.Item>
                    </DropdownButton>
                    <DropdownButton
                        className="mb-2"
                        variant="outline-secondary"
                        title={"Condition: " +rows.sortCondition || "Condition"}
                        id="input-group-dropdown-1"
                    >
                        <Dropdown.Item onClick={()=> rows.setSortCondition('Equals')}>Equals</Dropdown.Item>
                        <Dropdown.Item onClick={()=> rows.setSortCondition('Include')}>Include</Dropdown.Item>
                        <Dropdown.Item onClick={()=> rows.setSortCondition('Over')}>Over</Dropdown.Item>
                        <Dropdown.Item onClick={()=> rows.setSortCondition('Less then')}>Less then</Dropdown.Item>
                    </DropdownButton>
                    <InputGroup >
                        <Form.Control
                            placeholder="Value to filter"
                            aria-label="Value to filter"
                            aria-describedby="Value to filter"
                            value={rows.sortValue} onChange={e => rows.setSortValue(e.target.value)}
                        />
                    </InputGroup>
                </Card.Body>
            </Card>
        </div>
    );
});

export default Sorting;
