    }

    return (
        <div>
            <Table className="my-auto" striped bordered hover>
                <thead>
                <tr>
                    <th>Date</th>
                    <th>Name</th>
                    <th>Count</th>
                    <th>Distance</th>
                </tr>
                </thead>
                <tbody>
                {/*{rows.map(row => (
                    <tr key={row.col_id}>
                        <td className="col-3">{row.col_date}</td>
                        <td className="col-3">{row.col_name}</td>
                        <td className="col-3">{row.col_count}</td>
                        <td className="col-3">{row.col_distance}</td>
                    </tr>
                ))}*/}
                {tableRows.map(row => (
                    <tr key={row.col_id}>
                        <td className="col-3">{row.col_date}</td>
                        <td className="col-3">{row.col_name}</td>
                        <td className="col-3">{row.col_count}</td>
                        <td className="col-3">{row.col_distance}</td>
                    </tr>
                ))}
                </tbody>
            </Table>
            <Pagination_bamf rowsPerPage={rowsPerPage} totalRows={tableRows.length}/>
        </div>
    );
});

export default Table_bamf;
