import {Table} from "react-bootstrap";

import React, {useEffect,useState} from "react";

const Table_bamf = () => {

    const [rows, setRows] = useState([])

    const getTable = async(req, res) => {


        try{
            const response = await fetch("http://localhost:5000/rows")
            const jsonData = await response.json()

            setRows(jsonData)
        }
        catch (err){
            console.error(err.message)
        }
    }

    useEffect(() => {
        getTable();
    },[])

    console.log(rows)


    return (
        <Table className="my-auto" striped bordered hover>
            <thead>
            <tr>
                <th>Date</th>
                <th>Name</th>
                <th>Count</th>
                <th>Distance</th>
            </tr>
            </thead>
            <tbody>
                {rows.map(row => (
                    <tr key={row.col_id}>
                        <td className="col-3">{row.col_date}</td>
                        <td className="col-3">{row.col_name}</td>
                        <td className="col-3">{row.col_count}</td>
                        <td className="col-3">{row.col_distance}</td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
};

export default Table_bamf;
